    Shiboken::AutoDecRef arglist(Shiboken::makeTuple(type, msg));
    Shiboken::AutoDecRef ret(PyObject_CallObject(qtmsghandler, arglist));
}
static void QtCoreModuleExit()
{
    PySide::SignalManager::instance().clear();
}

