static bool
PyObject_inherits_internal(PyTypeObject *objType, const char *class_name)
{
    if (strcmp(objType->tp_name, class_name) == 0)
        return true;

    PyTypeObject* base = (objType)->tp_base;
    if (base == 0)
        return false;
    return PyObject_inherits_internal(base, class_name);
}

